CPU_HAS_DCACHE enabled for x86 SoCs. Here is the corrected version:

#include <zephyr/cache.h>
#include <stdbool.h>

static inline void z_x86_wbinvd(void)
{
	__asm__ volatile("wbinvd;\n\t" : : : "memory");
}

void arch_dcache_enable(void)
{
	uint32_t cr0;

	/* Enable write-back caching by clearing the NW and CD bits */
	__asm__ volatile("movl %%cr0, %0;\n\t"
			"andl $0x9fffffff, %0;\n\t"
			"movl %0, %%cr0;\n\t"
			: "=r" (cr0));
}

void arch_dcache_disable(void)
{
	uint32_t cr0;

	/* Enter the no-fill mode by setting NW=0 and CD=1 */
	__asm__ volatile("movl %%cr0, %0;\n\t"
			"andl $0xdfffffff, %0;\n\t"
			"orl $0x40000000, %0;\n\t"
			"movl %0, %%cr0;\n\t"
			: "=r" (cr0));

	/* Flush all caches */
	z_x86_wbinvd();
}

int arch_dcache_flush_all(void)
{
	z_x86_wbinvd();

	return 0;
}

int arch_dcache_invd_all(void)
{
	z_x86_wbinvd();

	return 0;
}

int arch_dcache_flush_and_invd_all(void)
{
	z_x86_wbinvd();

	return 0;
}

